#include <stdio.h>

int main() {
    int n, num, min, max;
    scanf("%d", &n);
    scanf("%d", &num);
    min = max = num;
    for (int i = 1; i < n; i++) {
        scanf("%d", &num);
        if (num < min) min = num;
        if (num > max) max = num;
    }
    printf("%d %d\n", min, max);
    return 0;
}

#include <stdio.h>

int main() {
    int n;
    double scores[1000], max = 0, sum = 0;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%lf", &scores[i]);
        if (scores[i] > max) max = scores[i];
    }
    for (int i = 0; i < n; i++) {
        sum += scores[i] / max * 100;
    }
    printf("%.2lf\n", sum / n);
    return 0;
}

